from collections import deque
def shortestBridge(A):
    R, C = len(A), len(A[0])
    q = deque()

    def dfs(A, i, j):
        if i>=R or i<0 or j>=C or j<0 or A[i][j] == -1:
            return

        if A[i][j] == 1:
            A[i][j] = -1
            dfs(A, i + 1, j)
            dfs(A, i - 1, j)
            dfs(A, i, j + 1)
            dfs(A, i, j - 1)
        elif A[i][j] == 0:
            q.append((i,j,1))

    def findFirst():
        for i in range(R):
            for j in range(C):
                if(A[i][j] == 1):
                    dfs(A, i, j)
                    return

    findFirst()
    directions = [(-1,0),(1,0),(0,-1),(0,1)]
    while q:
        r,c,d = q.popleft()
        for i,j in directions:
            if(r+i>=R or r+i<0 or c+j>=C or c+j<0 or A[r+i][c+j]==-1): continue

            if(A[r+i][c+j]==1):
                return d
            else:
                q.append((r+i, c+j, d+1))
                A[r + i][c + j] = -1

